class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> diff(nums.size());

        for (int i = 0; i < nums.size(); ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long ans = 0;
        long long res = 0;

        for (int i = 0; i < nums.size(); ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long long op = abs(diff[i]) - abs(res);
                if (op > 0) {
                    ans += op;
                }
                res = diff[i];
            } else {
                ans += abs(diff[i]);
                res = diff[i];
            }
        }

        return ans;
    }
};
