class Solution {
public:
    long long minimumOperations(vector<int>& ar, vector<int>& t) {
        int n = ar.size();
        vector<int> a(n);
        for(int i = 0 ; i < n ; i++) a[i] = t[i]-ar[i];

        long long ans = abs(a[0]);
        for(int i = 1 ; i < n ; i++){
            if(a[i] >= 0 && a[i-1] >= 0){ // both +ve
                ans = ans + max(0,a[i]-a[i-1]);
            }
            else if(a[i] >= 0 && a[i-1] < 0){
                ans = ans + a[i];
            }
            else if(a[i] < 0 && a[i-1] >= 0){
                ans = ans + abs(a[i]);
            }
            else{
                ans = ans + max(0, abs(a[i])-abs(a[i-1]));
            }
        }
        return ans;
    }
};
