class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        n=len(target)
        A = [target[i]-nums[i] for i in range(n)]
        maxp,minp = 0,0
        grps = []
        for i in range(len(A)):
            if grps and grps[-1] and grps[-1][-1] > 0 and A[i]>0: grps[-1].append(A[i])
            elif grps and grps[-1] and grps[-1][-1] <0 and A[i]<0: grps[-1].append(A[i])
            else: grps.append([A[i]])

        grps = [[ abs(x) for x in grp ] for grp in grps if grp]

        def solve(grp):
            curmax = 0 
            ans = 0
            for x in grp:
                if x<curmax: 
                    curmax=x
                else:
                    ans+=x-curmax
                    curmax=x
            return ans


        return sum(solve(grp) for grp in grps)
