class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        long long int n = a.size();
        vector<long long int> dif(n);
        for(int i=0;i<n;i++){
            dif[i] = b[i] - a[i];
        }
        long long int ans = abs(dif[0]), mn = ans;
        for(int i=1;i<n;i++){
            if(dif[i - 1] == 0)
            {
                ans += abs(dif[i]);
                mn = abs(dif[i]);
            }
            else if(dif[i-1] >= 0 && dif[i] >= 0){
                if(dif[i-1] < dif[i])
                ans += max(dif[i] - dif[i-1], 0LL);
                mn = min(mn,dif[i]);
            }
            else if(dif[i-1] >= 0 && dif[i] < 0){
                ans += abs(dif[i]);
                mn = abs(dif[i]);
            }
            else if(dif[i-1] < 0 && dif[i] >= 0){
                ans += abs(dif[i]);
                mn = abs(dif[i]);
            }
            else{
                if(abs(dif[i-1]) < abs(dif[i]))
                ans += max(abs(abs(dif[i]) - abs(dif[i-1])), 0LL);
                mn = min(mn,abs(dif[i]));
            }
            cout << ans << endl;
        }
        return ans;
    }
};
