class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n,0);
        long long operations = 0;
        for(int i=0; i<n; i++) {
            diff[i] = target[i] - nums[i];
            // cout<< diff[i] << " ";
        }
        // cout<<endl;

        int i=0, nexti, j; 
        bool flag = true;
        while(i<n) {
            flag = true;
            if(diff[i]>0) {
                j = i+1;
                int mini = diff[i];
                while(j<n && diff[j]>0) {
                    mini = min(mini, diff[j]);
                    diff[j] -= mini;
                    if(diff[j]>0 && flag) {
                        nexti = j;
                        flag = false;
                    } 
                    // if(diff[j]>diff[i] && flag) {
                    //     nexti = j;
                    //     flag = false;
                    // }
                    // diff[j] -= diff[i];
                    j++;
                }
            } else if(diff[i]<0) {
                j = i+1;
                int maxi = diff[i];
                while(j<n && diff[j]<0) {
                    maxi = max(maxi, diff[j]);
                    diff[j] -= maxi;
                    if(diff[j]<0 && flag) {
                        nexti = j;
                        flag = false;
                    }
                    // if(diff[j]<diff[i] && flag) {
                    //     nexti = j;
                    //     flag = false;
                    // }
                    // diff[j] -= diff[i];
                    j++;
                }
            }
            operations += abs(diff[i]);
            diff[i] = 0;

            if(flag) {
                i++;
            } else {
                i = nexti;
            }
        }
        return operations;
    }
};
