class Solution {
public:
    long long func1(int s, int e, vector<int> &v){
        long long ans = v[s];
        int prev = v[s];
        for (int i = s+1; i <=e ; i++) {
            if (v[i] > prev) {
                ans += v[i] - prev;
            }
            prev = v[i];
        }
        return ans;
    }
    long long func2(int s, int e, vector<int> &v){
        long long ans = abs(v[s]);
        int prev = abs(v[s]);
        for (int i = s+1; i <=e ; i++) {
            if (abs(v[i]) > prev) {
                ans += abs(v[i]) - prev;
            }
            prev = abs(v[i]);
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> v(n);
        for(int i=0;i<n;i++) v[i] = target[i]-nums[i];

        for(int i=0;i<n;){
            if(v[i] >= 0){
                int j = i+1;
                while(j<n && v[j] >= 0) j++;
                ans += func1(i,j-1,v);
                i=j;
            }
            else{
                int j = i+1;
                while(j<n && v[j] < 0) j++;
                ans += func2(i,j-1,v);
                i=j;
            }
        }
        return ans;
    }
};
