class Solution {
public:
    long long help(vector<int>& b,int l,int h)
    {
        if(l>h)
            return 0;
        if(l==h)
            return abs(b[l]);
        long long ans=abs(b[l]);
        for(int i=l+1;i<=h;i++)
        {
            if(abs(b[i])>abs(b[i-1]))
                ans+=abs(b[i])-abs(b[i-1]);
        }
        return ans;
    }
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        for(int i=0;i<n;i++)
        {
            b[i]-=a[i];
        }
        int i=0;
        long long ans=0;
        int prev=-2;
        while(i<n)
        {
            int s=i;
            while(i<n && b[i]<0)
            {
                i++;
            }
            int e=i-1;
            ans+=help(b,s,e);
            while(i<n && b[i]==0)
            {
                i++;
            }
            s=i;
            while(i<n && b[i]>0)
            {
                i++;
            }
            e=i-1;
            ans+=help(b,s,e);
        }
        return ans;
    }
};
