class Solution:
    def minimumOperations(self, nn: List[int], tt: List[int]) -> int:
        tgt = [tt[i]-nn[i] for i in range(len(nn))]

        # 分割成正负同向的段
        def mycut(lst1):
            block = []
            tmp = []
            for i in range(len(lst1)):
                if lst1[i]==0:
                    if tmp!=[]:
                        block.append(tmp)
                    tmp = []
                elif tmp==[] or lst1[i]*tmp[-1]>=0:
                    tmp.append(lst1[i])
                else:
                    block.append(tmp)
                    tmp = [lst1[i]]
            block.append(tmp)
            return block

        def mycost(lst):
            if lst==[]:
                return 0
            if lst[0]<0:
                lst = [-i for i in lst]
            lstlst = lst[:]

            idx = lst.index(max(lst))
            for i in range(1,idx):
                lstlst[i] = max(lstlst[i], lstlst[i-1])
            for i in range(len(lst)-2, idx, -1):
                lstlst[i] = max(lstlst[i], lstlst[i+1])

            lstdiff = [lstlst[i]-lst[i] for i in range(len(lst))]
            subblock = mycut(lstdiff)
            #print(subblock, lst, lstdiff)
            return max(lst) + sum([mycost(i) for i in subblock])

        block0 = mycut(tgt)
        return sum([mycost(i) for i in block0])
