typedef long long ll;
#define repn(i, n, e) for(ll i = n; i < e; i++)
#define rep(i, n, e) for(ll i = n; i <= e; i++)
#define sor(a) sort(a.begin(), a.end())
#define pb push_back
#define vec(a) vector<int> a
#define mod 1000000007

class Solution {
public:
    ll minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
         ll n = nums.size();
        vec(diff(n));

        repn(i, 0, n) {
            diff[i] = target[i] - nums[i];
        }

        ll ans = 0;
        ll curr = 0;
        repn(i, 0, n) {
            if (i == 0 || (diff[i - 1] < 0 && diff[i] < 0) || (diff[i - 1] > 0 && diff[i] > 0)) {
                ll op = abs(diff[i]) - abs(curr);
                if (op > 0) {
                    ans += op;
                }
                curr = diff[i];
            } else {
                ans += abs(diff[i]);
                curr = diff[i];
            }
        }

        return ans;
    }
};
