typedef long long ll;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        int prev =0;
        ll pans=0, nans=0;
        for (size_t i = 0; i < t.size(); ++i) {
            t[i] -= nums[i];
            if (t[i]>0)
            {
                if (prev<0) prev=0;
                int addl = max(0, t[i]-prev);
            pans += addl;
            prev = t[i];}
            else if (t[i]<0) {
                if (prev>0) prev=0;
                pans += max(0, -t[i]+prev);
                prev = t[i];
            }
            else prev=t[i];
        }
        return pans;
    }
};
