class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        ts = [nums[i]-target[i] for i in range(n)]
        ans = abs(ts[0])
        for i in range(1, n):
            if ts[i]*ts[i-1] > 0:
                if ts[i] > 0 and ts[i] > ts[i-1]:
                    ans += ts[i]-ts[i-1]
                elif ts[i] < 0 and ts[i] < ts[i-1]:
                    ans += ts[i-1]-ts[i]
            else:
                ans += abs(ts[i])
        return ans
