class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<long long> v(n + 5, 0);
        for (int i = 1; i <= n; i ++ ) v[i] = nums[i - 1] - target[i - 1];
        long long pre = 0;
        for (int i = 1; i <= n; i ++ ) {
            int x = v[i];
            if (pre > 0 && x > 0) {
                if (x >= pre) ans += x - pre;
            }
            else if (pre < 0 && x < 0) {
                if (x <= pre) ans += abs(x) - abs(pre);
            }
            else if (pre > 0 && x < 0) {
                ans += abs(x);
            }
            else if (pre < 0 && x > 0) {
                ans += x;
            }
            else if (pre == 0) {
                ans += abs(x) - pre;
            }
            pre = x;
        }
        return ans;
    }
};
