class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int[] pre=new int[nums.length];
        for(int i=0;i<nums.length;i++)
        {
            pre[i]=target[i]-nums[i];
        }
        long ans=0;
        int prev=-1;
        if(pre[0]>=0)
        prev=1;
        int min=0;
        int curr=pre[0];
        int i=0;
        while(i<nums.length)
        {
            if(prev==1)
            {
                if(pre[i]>=0)
                {
                    if(curr>pre[i])
                    {
                        ans+=(long)(curr-min);
                        min=pre[i];
                        curr=min;
                    }
                    else
                    curr=Math.max(pre[i],curr);
                }
                else
                {
                    ans+=(long)(curr-min);
                    prev=-1;
                    min=0;
                    curr=Math.abs(pre[i]);
                }
            }
            else
            {
                if(pre[i]<0)
                {
                    int n=Math.abs(pre[i]);
                    if(curr>=n)
                    {
                        ans+=(long)(curr-min);
                        min=n;
                        curr=min;
                    }
                    else
                    curr=Math.max(n,curr);
                }
                else
                {
                    ans+=(long)(curr-min);
                    prev=1;
                    min=0;
                    curr=Math.abs(pre[i]);
                }
            }
            // System.out.println(curr+" "+i+" "+pre[i]+" "+ans+" "+min);
            i++;
        }
        ans+=(curr-min);
        return ans;
    }
}
