class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long ans = 0;
        LinkedList<Integer> list = new LinkedList<>();
        for(int i = 0; i < n; i++){
            nums[i] -= target[i];
            if(nums[i] != 0) list.add(i);
        }
        //boolean数组记录当前nums是否已经为空了
        boolean[] isZero = new boolean[n];
        while(!list.isEmpty()){
            int i = list.poll(), j = i, start = i;
            if(isZero[i]) continue;
            //当cnt>0 > cnt并且最小的，将范围内全部减小cnt
            int cnt = nums[i];  //要删除的个数
            if(cnt > 0){
                int min = cnt;
                while(j < n && nums[j] > 0){
                    if(nums[j] < min) {
                        min = nums[j];
                    }
                    j++;
                }
                while(i < j) {
                    //将范围内的全部减小min
                    nums[i] -= min;
                    if (nums[i] == 0)
                        isZero[i] = true;
                    i++;
                }
                ans += min;
                if(cnt != min)
                    list.addFirst(start);
            }
            else{
                int mx = cnt;
                while(j < n && nums[j] < 0){
                    if(nums[j] > mx){
                        mx = nums[j];
                    }
                    j++;
                }
                while(i < j){
                    nums[i] -= mx;
                    if(nums[i] == 0)
                        isZero[i] = true;
                    i++;
                }
                ans -= mx;
                if(mx != cnt)
                    list.addFirst(start);
            }
        }
        return ans;
    }
}
