class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ops = abs(target[0] - nums[0]);

        for (int i = 1, prev = target[0] - nums[0]; i < n; ++i) {
            int diff = target[i] - nums[i];
            if (prev > 0 && diff > 0) {
                if (diff > prev) {
                    ops += diff - prev;
                }
                prev = diff;
            }
            else if (prev < 0 && diff < 0) {
                if (diff < prev) {
                    ops += prev - diff;
                }
                prev = diff;
            }
            else {
                ops += abs(diff);
                prev = diff;
            }
        }
        return ops;
    }
};
