class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> s(n);
        for(int i=0;i<n;i++)
        {
            s[i] = target[i]-nums[i];
        }
        ans = (long long)abs(s[0]);
        for(int i=0;i<n-1;i++)
        {
            if((long long)s[i+1]*s[i]>(long long)0)
            {
                if(abs(s[i+1]) >= abs(s[i]))ans += abs(s[i+1]-s[i]);
            }
            else if((long long)s[i+1]*s[i]<=(long long)0)
            {
                ans += abs(s[i+1]);
            }
        }
        return ans;
    }
};
