using ll=long long;
class Solution {
public:
    long long minimumOperations(vector<int>& b, vector<int>& t) {
        int n=b.size();
        vector<ll> a(n);
        for(int i=0;i<n;i++){
            a[i]=1ll*t[i]-1ll*b[i];
            // cout<<a[i]<<" ";
        }
        // cout<<endl;
        // 1 -2 2
        ll pre=0;
        ll cur=0;
        ll mx=0;
        ll res=0;
        while(cur<n){
            if(a[cur]>=0){
                pre=0;
                while(cur<n&&a[cur]>=0){
                    while(cur+1<n&&a[cur+1]>=0&&a[cur+1]>=a[cur])cur++;
                    mx=a[cur];res+=mx;
                    // cout<<"# "<<mx<<", ";
                    while(cur+1<n&&a[cur+1]>=0&&a[cur+1]<=a[cur])cur++;
                    res-=pre;
                    pre=a[cur];
                    if(cur+1>=n||a[cur+1]<0)break;
                }
                if(cur+1>=n)break;
                if(a[cur]>=0)cur++;
            }else{
                pre=0;
                while(cur<n&&a[cur]<=0){
                    while(cur+1<n&&a[cur+1]<=0&&a[cur+1]<=a[cur])cur++;
                    mx=a[cur];res+=-mx;
                    // cout<<"# "<<-mx<<", ";
                    while(cur+1<n&&a[cur+1]<=0&&a[cur+1]>=a[cur])cur++;
                    res-=-pre;
                    pre=a[cur];
                    if(cur+1>=n||a[cur+1]>0)break;
                }
                if(cur+1>=n)break;
                if(a[cur]<=0)cur++;
            }
        }
        return res;
    }
};
