class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        int n = nums.size();
        int last = 0;
        for(int i=0;i<n;i++){
            if(target[i] == nums[i]){
                last = 0;
            }
            if(target[i] > nums[i]){
                if(last <= 0){
                    res += target[i] - nums[i];
                    last = target[i] - nums[i];
                }
                else if(last>0){
                    if((target[i] - nums[i]) >= last){
                        res += target[i] - nums[i] - last;
                    }
                    last = target[i] - nums[i];
                }
            } 
            if(target[i] < nums[i]){
                if(last >= 0){
                    res += nums[i] - target[i];
                    last = target[i] - nums[i];
                }
                else if(last<0){
                    if((target[i] - nums[i]) <= last){
                        res += abs(target[i] - nums[i] - last);
                    }
                    last = target[i] - nums[i];
                }
            }
            //cout<<res<<endl;
        }
        return res;
    }
};
