        // return ans;
        vector<int>diff(n);
        for(int i=0;i<n;++i)
            diff[i]=target[i]-nums[i];
        long long cur=0;
        for(int i=0;i<n;++i)
        {
            if(!i || (diff[i-1]>0 && diff[i]>0) || (diff[i-1]<0 && diff[i]<0))
            {
                long long ct=abs(diff[i])-abs(cur);
                if(ct>0)
                    ans+=ct;
