class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size();
        vector<long long> d(n);

        long long i = 0;

        while (i < n) {
            d[i] = static_cast<long long>(target[i]) - static_cast<long long>(nums[i]);
            ++i;
        }

        long long t = 0; 
        long long c = 0; 

        i = 0;
        while (i < n) {
            long long a = (i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)) 
                    ? abs(d[i]) - abs(c)
                    : abs(d[i]);

            t += (i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0))
                    ? (a > 0 ? a : 0)
                    : a;

            c = d[i];
            ++i;
        }

        return t;
    }
};
