#define ll long long
class Solution {
public:
    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        ll res = 0, aux = 0, n = nums.size();
        map<int, int> mp;
        function<void()> solve = [&]() {
            for (int i = 0; i < n; ++i) {
                mp[i] = target[i] - nums[i];
            }
        };
        function<void()> solve2 = [&]() {
            for (int i = 1; i <= n; ++i) {
                if (i - 1 == 0 || (mp[i - 2] > 0 && mp[i - 1] > 0) || (mp[i - 2] < 0 && mp[i - 1] < 0)) {
                    ll x = abs(mp[i - 1]) - abs(aux);
                    res += (x > 0) ? x : 0;
                    aux = mp[i - 1];
                } else {
                    res += abs(mp[i - 1]);
                    aux = mp[i - 1];
                }
            }
        };
        solve(); solve2();
        return res;
    }
};
