class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> vec(nums.size(),0);
        long long a=0,b=nums.size(),c=0,d=0;
        for(int j=0;j<b;j++){
            vec[j]=target[j]-nums[j];
        }
        a=abs(vec[0]);
        c=a;
        if(vec[0]>=0)
            d=1;
        for(int k=1;k<b;k++){
            if(vec[k]<0 && d==1){
                a+=abs(vec[k]);
                c=abs(vec[k]);
                d=0;
                continue;
            }
            if(vec[k]>=0 && d==0){
                a+=abs(vec[k]);
                c=abs(vec[k]);
                d=1;
                continue;
            }
            if(c>=abs(vec[k])){
                c=abs(vec[k]);
                continue;
            }
            else{
                a+=abs(vec[k])-c;
                c=abs(vec[k]);
            }
        }
        return a;        
    }
};
