class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        D = [T-N for N,T in zip(nums, target)]
        CD = 0
        O = 0
        for i in range(len(D)):
            if i == 0 or (D[i-1]*D[i]>0):
                O += max(0,abs(D[i])-abs(CD))
                CD = D[i]
            else:
                O += abs(D[i])
                CD = D[i]
        return O
