class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n=nums.size();
        long long prev=0;
        vector<long long> dp(n+1,0);
        vector<long long> diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
        // vector<int> pos;
        // vector<int> neg;
        // for(int i=0;i<n;i++){
        //     if(diff[i]>=0){
        //         pos.push_back(diff[i]);
        //     }
        //     else{
        //         neg.push_back(diff[i]);
        //     }
        // }

        dp[0]=abs(0-diff[0]);
        for(int i=1;i<n;i++){
            if(diff[i]>=0){
                if(diff[i-1]>=0){
                    if(diff[i]>diff[i-1]){
                        dp[i]=dp[i-1]+(diff[i]-diff[i-1]);
                    }
                    else{
                        dp[i]=dp[i-1];
                    }
                }
                else{
                    dp[i]=dp[i-1]+diff[i];
                }
            }
            else{
                if(diff[i-1]<0){
                    long long abs1=0-diff[i];
                    long long abs2=0-diff[i-1];
                    if(abs1>abs2){
                        dp[i]=dp[i-1]+(abs1-abs2);
                    }
                    else{
                        dp[i]=dp[i-1];
                    }
                }
                else{
                    dp[i]=dp[i-1]+(0-diff[i]);
                }
            }
        }

        return dp[n-1];
    }
};
