class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] d = new long[n];

        for (int i = 0; i < n; i++) {
            d[i] = (long) target[i] - nums[i];
        }

        long ops = 0;
        long seg = 0;

        for (int i = 0; i < n; i++) {
            if (i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)) {
                long add = Math.abs(d[i]) - Math.abs(seg);
                if (add > 0) {
                    ops += add;
                }
                seg = d[i];
            } else {
                ops += Math.abs(d[i]);
                seg = d[i];
            }
        }

        return ops;

    }
}
