class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;

        long ans = 0;
        for(int i = 0;i<n;i++){
            nums[i] = nums[i]-target[i];
            if(i>0){
                if((long)nums[i]*(long)nums[i-1]>=0l){
                    if(Math.abs(nums[i])<=Math.abs(nums[i-1])) ans+=0;
                    else ans+= Math.abs(nums[i]-nums[i-1]);
                }else{
                    ans+=Math.abs(nums[i]);
                }
            }else{
                ans+=Math.abs(nums[i]);
            }
        }
        return ans;
    }
}
