class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        ans = cc = 0
        neg = False
        for i in range(n):
            diff = nums[i] - target[i]
            if diff >= 0:
                if neg:
                    cc = 0
                neg = False
            else:
                if not neg:
                    cc = 0
                neg = True

            diff = abs(diff)
            cc = min(cc, diff)
            if cc == diff:
                continue
            ans += diff - cc
            cc = diff
        return ans
