class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        using ll = long long;
        vector<ll> diff(nums.size(), 0);
        for(ll i = 0; i < nums.size(); i++) {
            diff[i] = target[i] - nums[i];
        }
        ll tot = 0;
        ll last = 0;
        for(int i = 0; i < nums.size(); i++) {
            ll req = diff[i];
            if (req >= 0) {
                if (diff[i] <= last) {
                    last = diff[i];
                }
                else {
                    ll used = diff[i] - max(0ll, last);
                    last = diff[i];
                    tot += used;
                }
            }
            else {
                if (diff[i] >= last) {
                    last = diff[i];
                }
                else {
                    ll used = min(0ll, last) - diff[i];
                    last = diff[i];
                    tot += used;
                }
            }
        }
        return tot;
    }
};
