#include <vector>
#include <cmath>
#include <cstdlib>
using namespace std;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> differences(n);

        auto computeDifference = [](int a, int b) -> int {
            return b - a;
        };

        auto updateDifference = [](int& diff, int value) {
            diff = value;
        };

        auto calculateAdditionalOps = [](int currentDiff, int segmentDiff) -> int {
            return abs(currentDiff) - abs(segmentDiff);
        };

        int i = 0;
        while (i < n) {
            updateDifference(differences[i], computeDifference(nums[i], target[i]));
            i++;
        }

        long long operationCount = 0;
        int segmentDifference = 0;

        i = 0;
        while (i < n) {
            if (i == 0 || (differences[i - 1] > 0 && differences[i] > 0) || (differences[i - 1] < 0 && differences[i] < 0)) {
                int additionalOps = calculateAdditionalOps(differences[i], segmentDifference);
                if (additionalOps > 0) {
                    operationCount += additionalOps;
                }
                segmentDifference = differences[i];
            } else {
                operationCount += abs(differences[i]);
                segmentDifference = differences[i];
            }
            i++;
        }

        return operationCount;
    }

private: 
    int findIndex(vector<int>& arr, int target) {
        int left = 0;
        int right = arr.size() - 1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (arr[mid] == target) {
                return mid;
            } else if (arr[mid] < target) {
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return -1;  
    }
};
