class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long N = nums.size();
        vector<long long> diff(N);

        long long ops = 0;
        long long currDiff = 0;

        for (int i = 0; i < N; ++i) {
            diff[i] = target[i] - nums[i];
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int extraOps = abs(diff[i]) - abs(currDiff);
                currDiff = diff[i];
                if (extraOps > 0) ops += extraOps;
            } else {
                currDiff = diff[i];
                ops += abs(diff[i]);
            }
        }

        return ops;
    }
};
