class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans=0;
        int pre = 0;
        for(int i=0;i<n;i++){
            target[i]-=nums[i];
            if(target[i]>pre && pre>=0){
                ans+=target[i]-pre;
            }else if(target[i]>pre && pre<0 && target[i]>0){
                ans+=target[i];
            }
            else if(target[i]<pre&& pre<0){
                ans+= pre-target[i];
            }else if(target[i]<pre&& pre>=0 && target[i]<0){
                ans-= target[i];
            }
            pre =target[i];

        }
        return ans;
    }
    // 0 -3 1 9 -3 -1 -5 -3 -4 -2 
    //    3+1+8+3
};
