class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
            // cout<<diff[i]<<" ";
        }
        // cout<<"\n";
        long long ans=0;
        bool neg=false;
        if(diff[0]>0)neg=true;
        for(int i=0;i<n;i++){
            if(diff[i]<=0 && !neg){
                ans+=abs(diff[i]);
                neg=true;
            }else if(diff[i]>=0 && neg){
                ans+=diff[i];
                neg=false;
            }
            else if(neg && abs(diff[i])>abs(diff[i-1])){
                ans+=abs(diff[i])-abs(diff[i-1]);
            }else if(!neg && diff[i]>diff[i-1]){
                ans+=diff[i]-diff[i-1];
            }
        }
        return ans;
    }
};
