class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(nums)
        dif=[]
        for i in range(n):
            dif.append(nums[i]-target[i])
        print(dif)
        ans=0
        for i in range(n):
            if(dif[i]==0):continue
            if(i==0):ans+=abs(dif[i])
            elif(dif[i]*dif[i-1]<=0):
                ans+=abs(dif[i])
            elif(abs(dif[i])>abs(dif[i-1])):
                ans+=abs(dif[i]-dif[i-1])

        return ans

