class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long op = 0, n = nums.size(), cur = 0;
            vector <int> d(n);
            for(int i = 0; i < n; i++)d[i] = target[i]-nums[i];
            for(int i = 0; i < n; i++){
                    if(i == 0){
                            int nop = abs(d[i])-abs(cur);
                            if(nop > 0)op += nop;
                    }
                    else if(i > 0 && d[i] < 0 && d[i-1] < 0){
                            int nop = abs(d[i])-abs(cur);
                            if(nop > 0)op += nop;
                    }
                    else if(i > 0 && d[i] > 0 && d[i-1] > 0){
                            int nop = abs(d[i])-abs(cur);
                            if(nop > 0)op += nop;
                    }
                    else op += abs(d[i]);
                    cur = d[i];
            }
            return op;
    }
};
