class Solution {
public:
    vector<int>v;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long cnt=0;
        for(int i=0;i<n;i++) v.push_back(-nums[i]+target[i]);
        for(int i=0;i<n;i++)
        {
            if(v[i]>0)
            {
                vector<int>vc;
                vc.clear();
                int ans=0;
                int j=i;
                while(v[j]>0)
                {
                    vc.push_back(v[j]);
                    j++;
                    if(j==n) break;
                }
                i=j-1;
                int m=vc.size();
                ans=vc[0];
                for(int k=1;k<m;k++)
                {
                    ans+=max(vc[k]-vc[k-1],0);
                }
                cnt+=ans;
            }
            else if(v[i]<0)
            {
                vector<int>vc;
                vc.clear();
                int ans=0;
                int j=i;
                while(v[j]<0)
                {
                    vc.push_back(-v[j]);
                    j++;
                    if(j==n) break;
                }
                i=j-1;
                int m=vc.size();
                ans=vc[0];
                for(int k=1;k<m;k++)
                {
                    ans+=max(vc[k]-vc[k-1],0);
                }
                cnt+=ans;
            }
        }
        return cnt;
    }
};
