class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>dif(n,0);
        for(int i=0;i<n;i++) dif[i] = target[i] - nums[i];

        long long i = 0,res = 0;
        while(i < n){
            if(dif[i] == 0) {i++;continue;}
            int p = 0,j=i,c=dif[j];
            bool ent = false;
            while( j < n && dif[j] < 0 ){
                if(dif[j] > c) p += abs(dif[j] - c);
                c = dif[j];
                j++;
                ent =true;
            }
            res += p;
            if(ent) res += abs(c);
            p = 0;
            ent = false;
            if(j < n) c = dif[j];
            else c = 0;
            while( j < n && dif[j] > 0){
                if(dif[j] < c) p += abs(dif[j] - c);
                c = dif[j];
                j++;
                ent = true;
            } 
            res += p;
            if(ent) res += abs(c);
            i = j;
        }
        return res;
    }
};
