class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n=nums.size();
        vector<int>a(n);
        for(int i=0;i<n;i++) a[i]=target[i]-nums[i];
        long long ind=0,c=0;
        while(ind<n)
        {
            if(a[ind]>0)
            {
                vector<int>v;
                while(ind<n&&a[ind]>0) v.push_back(a[ind++]);
                int m=v[0];
                c+=m;
                for(int i=1;i<v.size();i++)
                {
                    if(m<v[i]) c+=v[i]-m;
                    m=v[i];
                }
            }
            else if(a[ind]<0)
            {
                vector<int>v;
                while(ind<n&&a[ind]<0) v.push_back(abs(a[ind++]));
                int m=v[0];
                c+=m;
                for(int i=1;i<v.size();i++)
                {
                    if(m<v[i]) c+=v[i]-m;
                    m=v[i];
                }
            }
            else ind++;
        }
        return c;
    }
};
