class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def count(target, total):
            i = 0
            diff = 0
            while i < len(target) and target[i] <= 0:
                i += 1
            if i < len(target):
                total += target[i]
                diff = target[i]
                i += 1
                while i < len(target):
                    if target[i] >= 0:
                        if target[i] > diff:
                            total += target[i] - diff
                        diff = target[i]
                    else:
                        diff = 0
                    i += 1
            return total

        for i in range(len(target)):
            target[i] = target[i] - nums[i]
        total = count(target, 0)
        for i in range(len(target)):
            target[i] *= -1
        total = count(target, total)

        return total
