class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> a;
        for(int i=0;i<n;i++){
            a.push_back(nums[i]-target[i]);
        }
        int val=0;
        long long cnt=0;
        int i=0;
        // for(auto i:a) {
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        while(i<n) {
            if(a[i]<0) {
                val=0;
                for(;i<n;i++){
                    if(a[i]>=0) break;
                    if(a[i]<val) {
                        cnt+=val-a[i];
                        val=a[i];
                    }
                    val=max(val,a[i]);
                }
            }
            else {
                val=0;
                for(;i<n;i++){
                    if(a[i]<0) break;
                    if(a[i]>val) {
                        cnt+=a[i]-val;
                        val=a[i];
                    }
                    val=min(val,a[i]);
                }
            }
            cout<<i<<" "<<cnt<<endl;
        }
        return cnt;
    }
};
