class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;

        int[] res = new int[n];
        for(int i=0;i<n;i++){
            res[i] = nums[i] - target[i];
        }
        long[] d = new long[n];
        d[0] = res[0];
        for(int i=1;i<n;i++){
            d[i] = res[i] - res[i-1];
        }

        long s1 = 0;
        long s2 = 0;
        for(int i=0;i<n;i++){
            if(d[i]>0){
                s1 += d[i];
            } else {
                s2+=d[i];
            }
        }
        return Math.max(s1, -s2);
    }
}
