class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = Math.abs(nums[0]-target[0]);
        int n = nums.length;
        long minDiffSoFar = ans;
        for(int i=1;i<n;i++){
            int curDiff = Math.abs(nums[i]-target[i]);
            if( (nums[i-1] > target[i-1] && nums[i] > target[i]) ||
            (nums[i-1]<target[i-1] && nums[i] < target[i]) ){
                if(curDiff > minDiffSoFar){
                    ans += (curDiff-minDiffSoFar);
                }
                minDiffSoFar = curDiff;

            } else{
                minDiffSoFar = curDiff;
                ans += curDiff;
            }
        }
        return ans;
    }
}
