typedef long long LL;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<int> c(n + 2, 0);
        for (int i = 1; i <= n; i++) c[i] = a[i - 1] - b[i - 1];
        LL cnt = abs(c[1]);
        for (int i = 2; i <= n; i++) {
            // cout << c[i] << ' ';
            if ((LL) c[i] * c[i - 1] < 0) cnt += abs(c[i]);
            else if (abs(c[i]) > abs(c[i - 1])) cnt += max(abs(c[i]) - abs(c[i - 1]), 0);
        }
        // cout << '\n' << "cnt= " << cnt << '\n';
        return cnt;
    }
};
