# define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        ll l = 0, r1 = 0, r2 = 0;
        ll ans = 0;
        for(int i = 0;i<n; ++i){
            ll diff = target[i] - nums[i];
            if(diff > 0){
                r2 = 0;
                if(r1 >= diff){
                    r1 = min(r1, diff);
                }else{
                    ans += diff - r1;
                    r1 = diff;
                }
            } else {
                r1 = 0;
                diff = -diff;
                if(r2 >= diff){
                    r2 = min(r2, diff);
                }else{
                    ans += diff - r2;
                    r2 = diff;
                }
            }
        } 

        return ans;
    }
};
