class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n = nums.size();
        vector<int> t(n, 0);

        // Calculate the differences
        int i = 0;
        while (i < n) {
            t[i] = tar[i] - nums[i];
            i++;
        }
        std::ios_base::sync_with_stdio(false);
        std::cin.tie(nullptr);
        std::cout.tie(nullptr);
        int pos = 0;
        long long m = 0, ans = 0;

        ans = absoluteValue(t[0]);
        m = ans;
        if (t[0] >= 0)
            pos = 1;

        i = 1;
        while (i < n) {
            if (t[i] < 0 && pos == 1) {
                ans += absoluteValue(t[i]);
                m = absoluteValue(t[i]);
                pos = 0;
            } else if (t[i] >= 0 && pos == 0) {
                ans += absoluteValue(t[i]);
                m = absoluteValue(t[i]);
                pos = 1;
            } else {
                if (m < absoluteValue(t[i])) {
                    ans += absoluteValue(t[i]) - m;
                    m = absoluteValue(t[i]);
                } else {
                    m = absoluteValue(t[i]);
                }
            }
            i++;
        }
        return ans;
    }

private:
    int absoluteValue(int x) const {
        std::ios_base::sync_with_stdio(false);
        std::cin.tie(nullptr);
        std::cout.tie(nullptr);
        return x < 0 ? -x : x;
    }
};
