class Solution {
public:
    long long secret(vector<long long >&diff){
        long long ans = 0;
        long long curr = 0;

        for (int i = 0; i < diff.size(); ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long long add = abs(diff[i]) - abs(curr);
                if (add > 0) {
                    ans += add;
                }
                curr = diff[i];
            } else {
                ans += abs(diff[i]);
                curr = diff[i];
            }
        }

        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> diff;
        for (int i = 0; i < nums.size(); ++i) {
            diff.push_back(target[i] - nums[i]);
        }
        return secret(diff);
    }
};
