class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            diff[i] = target[i] - nums[i];
        }
        long long ans = 0;
        long long current_diff = 0;
        for (int i = 0; i < diff.size(); i++) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) ||
                (diff[i - 1] < 0 && diff[i] < 0)) {
                int additional = abs(diff[i]) - abs(current_diff);
                if (additional > 0) {
                    ans += additional;
                }
                current_diff = diff[i];
            } else {
                ans += abs(diff[i]);
                current_diff = diff[i];
            }
        }
        return ans;
    }
};
