class Solution {
    void init(int num, int target, int &prev, int &diff, int &sc) {
        if (num < target) {
            prev = 1;
            diff = target - num;
            sc++;
        } else if (num > target) {
            prev = -1;
            diff = num - target;
            sc++;
        }
    }

    void solve(int num, int target, int &prev, int &diff, int &sc, long long &ops) {
        if ((num < target && prev == 1 && diff == target - num) ||
            (num > target && prev == -1 && diff == num - target)) {
            sc++;
        } else if (num < target && prev == 1) {
            handle1(num, target, diff, sc, ops);
        } else if (num > target && prev == -1) {
            handle2(num, target, diff, sc, ops);
        } else if (num == target) {
            ops += diff;
            reset(prev, diff, sc);
        } else {
            ops += diff;
            init(num, target, prev, diff, sc);
        }
    }

    void handle1(int num, int target, int &diff, int &sc, long long &ops) {
        if (target - num > diff) {
            sc = 1;
            diff = target - num;
        } else {
            ops += (diff - target + num);
            diff = target - num;
        }
    }

    void handle2(int num, int target, int &diff, int &sc, long long &ops) {
        if (num - target > diff) {
            sc = 1;
            diff = num - target;
        } else {
            ops += (diff + target - num);
            diff = num - target;
        }
    }

    void reset(int &prev, int &diff, int &sc) {
        prev = 0;
        diff = 0;
        sc = 0;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ops = 0;
        int n = nums.size();
        int prev = 0, diff = 0, sc = 0;

        for (int i = 0; i < n; i++) {
            if (prev == 0) {
                init(nums[i], target[i], prev, diff, sc);
            } else {
                solve(nums[i], target[i], prev, diff, sc, ops);
            }
        }

        return ops + diff;
    }
};
