class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        t = [target[i] - nums[i] for i in range(n)]
        s1=set()
        s1.add("1")
        pos = 0
        ans = 0
        m = abs(t[0])
        ans = m
        if t[0] >= 0:
            pos = 1
        else:
            pos = -1

        for i in range(1, n):
            if t[i] < 0 and pos == 1:
                ans += abs(t[i])
                m = abs(t[i])
                pos = -1
                continue
            if not s1:
                return 0
            if t[i] >= 0 and pos == -1:
                ans += abs(t[i])
                m = abs(t[i])
                pos = 1
                continue
            if m >= abs(t[i]):
                m = abs(t[i])
                continue
            else:
                ans += abs(t[i]) - m
                m = abs(t[i])
        if not s1:
            return 0
        return ans
