class Solution {
  public long minimumOperations(int[] hNums, int[] hTarget) {
    int hLength = hNums.length;
    int[] hDifferences = new int[hLength];

    for (int hI = 0; hI < hLength; ++hI) {
        hDifferences[hI] = hTarget[hI] - hNums[hI];
    }

    long hTotalOps = 0;
    int hSegmentDiff = 0;

    for (int hI = 0; hI < hLength; ++hI) {
        if (hI == 0 || (hDifferences[hI - 1] > 0 && hDifferences[hI] > 0) || (hDifferences[hI - 1] < 0 && hDifferences[hI] < 0)) {
            long hExtraOps = Math.abs(hDifferences[hI]) - Math.abs(hSegmentDiff);
            if (hExtraOps > 0) {
                hTotalOps += hExtraOps;
            }
            hSegmentDiff = hDifferences[hI];
        } else {
            hTotalOps += Math.abs(hDifferences[hI]);
            hSegmentDiff = hDifferences[hI];
        }
    }

    return hTotalOps;
}

}
