class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        for(int i = 0; i < n; i++) {
            a[i] -= b[i];
        }
        int ok = a[0] >= 0;
        long long i = 0, mx = 0, ans = 0, prev = 0;
        while(i < n) {
            int cur = a[i] >= 0;
            if(ok == cur) {
                if(abs(prev) > abs(a[i])) ans += abs(prev) - abs(a[i]);
                prev = abs(a[i]);
            }
            else {
                ans += prev;
                prev = abs(a[i]);
                ok = cur;
            }
            i++;
        }
        ans += prev;
        return ans;
    }
};
